mapper.select:
select c.class_idx ,c.instructor_idx ,c.class_title ,c.create_date ,c.class_thumbnail ,c.class_intro ,c.class_price ,c.class_category ,c.class_level ,c.total_lessons ,c.url ,c.accept_status ,m.user_id as instructor_id from class c left join member m on c.instructor_idx = m.idx
m.user_id like concat('%', #{q}, '%')
order by create_date desc limit #{start_row}, #{list_limit}

mapper.update:
update class
set accept_status = #{accept_status}
	,deny_reason = #{deny_reason}
where class_idx = #{class_idx}
