mapper.insert:
  INSERT INTO class_session (
    class_idx, 
    session_name, 
    session_index
) VALUES (
 	#{class_idx}, 
 	#{session_name}, 
 	#{session_index}
 )

mapper.select:
SELECT session_idx, class_idx, session_name, session_index
FROM class_session
WHERE class_idx = #{class_idx}

mapper.update:
UPDATE class_session
SET session_name = #{session_name}
WHERE session_idx = #{session_idx}
