mapper.select:
select p.post_idx ,p.board_code ,p.title ,p.author_idx ,(select m.nickname from member m where m.idx = p.author_idx) as author ,p.created_date ,p.update_date ,p.is_update ,p.view_count ,(select count(*) from comment c where c.post_idx = p.post_idx) as cnt_comment from post p
p.title like concat('%', #{q}, '%')
order by p.created_date desc, post_idx desc limit #{start_row}, #{list_limit}

mapper.insert:
  insert into post (
	    board_code, title, content, author_idx, created_date, view_count
	  ) values (
	    'BD01'
	    ,#{title}
	    ,#{content}
	    ,#{author_idx}
	    ,default
	    ,1
	  )

mapper.update:
update post 
set view_count = view_count + 1 
where post_idx = #{post_idx};

mapper.delete:
delete from post 
where post_idx = #{post_idx} 
and 
author_idx = #{idx}

mapper.update:
update post 
set title = #{vo.title}
,content = #{vo.content}
,is_update = true 
,update_date = now() 
where post_idx = #{vo.post_idx} and author_idx = #{idx}
