mapper.select:
	    SELECT
	    user_id,
	    password
	    FROM member
	    WHERE user_id = #{user_id}
        AND password = #{password}

select idx, user_id, password, user_name, nickname, email, member_type, profile_img
from member
where user_id = #{user_id}

SELECT user_id
FROM member
WHERE user_name = #{user_name} AND email = #{email}

SELECT password
FROM member
WHERE user_id = #{user_id} AND email = #{email}
