mapper.delete:
delete 
  from playlist
 where playlist_idx = #{playlistIdx} 
   and user_idx = #{userIdx}

mapper.update:
update playlist
   set 
       playlist_name = #{playlistName}
     , update_date = now()
 where playlist_idx = #{playlistIdx} 
   and user_idx = #{userIdx}

mapper.select:
select *
  from playlist
 where playlist_idx = #{playlistIdx}
   and user_idx = #{userIdx}
